import ProductInCartCard from "../components/ProductInCartCard";
import { useState, useEffect } from "react";



function Cart( { setCart, cart } ) {

    useEffect(() => {
        fetch("/get_cart_items")
        .then(r => r.json())
        .then(data => setCart(data))
    }, [])


    function handleClick() {
        fetch(`/delete_cart_item/${id}`, {
         method: 'DELETE',
         headers: {
             'Content-Type': 'application/json',
         },
        })
        .then(r => r.json)
        .then(r => setCart(cart.filter(item => item.id !== id)))
     }




    function removeProduct(name) {
        setCart(cart.filter(product => product.name !== name));
    }

    const items = cart.map(item => 
    <ProductInCartCard key={item.id} {...item} handleClick={handleClick} />
    )

    return (
        <div>{items}</div>
    )

}

export default Cart;
