function ProductInCartCard({ cart, setCart, id, name, price, image_path, removeProduct }) {

    function handleClick() {
       fetch(`/delete_cart_item/${id}`, {
        method: 'DELETE',
        headers: {
            'Content-Type': 'application/json',
        },
       })
       .then(r => r.json)
       .then(r => setCart(cart.filter(item => item.id !== id)))
    }


    return (
        <div className="cart-product-card">
            <img className="cart-product-image" src={image_path} alt={name} />
            <h1 className="cart-product-name">{name}</h1>
            <h2 className="cart-product-price">{price}</h2>
            <button onClick={handleClick}>Remove</button>
        </div>
    );
}

export default ProductInCartCard;
