import ProductInCartCard from "../components/ProductInCartCard";
import { useState, useEffect } from "react";



function Cart( { setCart, cart } ) {

    useEffect(() => {
        fetch("/get_cart_items")
        .then(r => r.json())
        .then(data => setCart(() => data))
    }, [])

    console.log(cart)





    function removeProduct(name) {
        setCart(cart.filter(product => product.name !== name));
    }

    const items = cart.map(item => 
    <ProductInCartCard key={item.id} {...item} removeProduct={removeProduct} />
    )

    return (
        <div>{items}</div>
    )

}

export default Cart;
