function ProductCard( { id, name, price, description, cart, setCart }) {

function handleClick() {
fetch(`/products/${id}`)
.then(r => r.json())
.then(data => setCart([...cart, data]))
.then(console.log(cart))
}



return (
    <div>
        <h1>{name}</h1>
        <h2>{price}</h2>
        <h4>{description}</h4>
        <button onClick={handleClick}>Add to Cart</button>
    </div>
)


} 

export default ProductCard;
